    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
            System.out.println("Cantidad de jugadores: " + sJ.getTamañoArray());

        if (sJ.getTamañoArray()>1) {
            SeleccionOponente sO = new SeleccionOponente(); 
            sO.setVisible(true);
            this.dispose();
        }else{
            JOptionPane.showMessageDialog(null, "Debe haber más de un jugador para poder jugar.");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jToggleButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jToggleButton2ActionPerformed
        // TODO add your handling code here:
        if(sJ.getLogs()!=null){
        Logs l = new Logs();
        l.setVisible(true);
        this.dispose();
        }else{
            JOptionPane.showMessageDialog(null,"Necesita tener al menos una partida jugada.");
        }
    }//GEN-LAST:event_jToggleButton2ActionPerformed

